import React, { useState, useRef } from "react";
import EmojiPicker from "emoji-picker-react";
import {
  Highlighter, Link
} from "lucide-react";


export default function ToolbarTiptap({ editor }) {
  const [showEmoji, setShowEmoji] = useState(false);
  const fileInputRef = useRef(null);
  const [refresh, setRefresh] = useState(false);


  React.useEffect(() => {
    if (!editor) return;
    const update = () => setRefresh((prev) => !prev);
    editor.on("transaction", update);
    return () => editor.off("transaction", update);
  }, [editor]);

  if (!editor) return null;


  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = () => {
      editor.chain().focus().setImage({ src: reader.result }).run();
    };
    reader.readAsDataURL(file);
  };

  const addImage = () => {
    fileInputRef.current.click();
  };

  const attachmentInputRef = useRef(null);

  const addAttachment = () => {
    attachmentInputRef.current.click();
  };

  const handleAttachmentChange = (e) => {
  const file = e.target.files[0];
  if (!file) return;

  const url = URL.createObjectURL(file);
  editor?.commands.setLink({ href: "https://google.com" });
editor?.commands.insertContent("Google Test");


  editor
    .chain()
    .focus()
    .insertContent(`ðŸ“Ž `)           // insert icon dulu
    .setLink({ href: url })        // jadikan bagian berikutnya sebagai LINK
    .insertContent(file.name)      // teks yang di-link
    .unsetLink()                   // stop link supaya tidak "merembet" ke teks berikutnya
    .run();
};

  const Bold = () => (
    <img
      src="data:image/svg+xml;base64,"
      alt="Bold"
      className="w-5 h-5"
    />
  );
  const Italic = () => (
    <img
      src="data:image/svg+xml;base64,"
      alt="Bold"
      className="w-5 h-5"
    />
  );
  const Underline = () => (
    <img
      src="data:image/svg+xml;base64,"
      alt="Bold"
      className="w-5 h-5"
    />
  );
  const Strikethrough = () => (
    <img
      src="data:image/svg+xml;base64,"
      alt="Bold"
      className="w-6 h-6"
    />
  );
  const ListOrdered = () => (
    <img
      src="data:image/svg+xml;base64,"
      alt="Bold"
      className="w-6 h-6"
    />
  );
  const List = () => (
    <img
      src="data:image/svg+xml;base64,"
      alt="Bold"
      className="w-6 h-6"
    />
  );
  const AlignLeft = () => (
    <img
      src="data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iMjAiIGhlaWdodD0iMTIiIHZpZXdCb3g9IjAgMCAyMCAxMiIgZmlsbD0ibm9uZSIgeG1sbnM9Imh0dHA6Ly93d3cudzMub3JnLzIwMDAvc3ZnIj4KPHBhdGggZD0iTTEgMUgxOU0xIDZIMTNNMSAxMUg1IiBzdHJva2U9IiMzMzMzMzMiIHN0cm9rZS13aWR0aD0iMiIgc3Ryb2tlLWxpbmVjYXA9InJvdW5kIiBzdHJva2UtbGluZWpvaW49InJvdW5kIi8+Cjwvc3ZnPgo="
      alt="Bold"
      className="w-6 h-6"
    />
  );
  const AlignCenter = () => (
    <img
      src="data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iMjAiIGhlaWdodD0iMTIiIHZpZXdCb3g9IjAgMCAyMCAxMiIgZmlsbD0ibm9uZSIgeG1sbnM9Imh0dHA6Ly93d3cudzMub3JnLzIwMDAvc3ZnIj4KPHBhdGggZD0iTTEgMUgxOU00IDZIMTZNOCAxMUgxMiIgc3Ryb2tlPSIjMzMzMzMzIiBzdHJva2Utd2lkdGg9IjIiIHN0cm9rZS1saW5lY2FwPSJyb3VuZCIgc3Ryb2tlLWxpbmVqb2luPSJyb3VuZCIvPgo8L3N2Zz4K"
      alt="Bold"
      className="w-6 h-6"
    />
  );
  const AlignRight = () => (
    <img
      src="data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iMjAiIGhlaWdodD0iMTIiIHZpZXdCb3g9IjAgMCAyMCAxMiIgZmlsbD0ibm9uZSIgeG1sbnM9Imh0dHA6Ly93d3cudzMub3JnLzIwMDAvc3ZnIj4KPHBhdGggZD0iTTE5IDFIMU0xOSA2SDdNMTkgMTFIMTUiIHN0cm9rZT0iIzMzMzMzMyIgc3Ryb2tlLXdpZHRoPSIyIiBzdHJva2UtbGluZWNhcD0icm91bmQiIHN0cm9rZS1saW5lam9pbj0icm91bmQiLz4KPC9zdmc+Cg=="
      alt="Bold"
      className="w-6 h-6"
    />
  );
  const AlignJustify = () => (
    <img
      src="data:image/svg+xml;base64,"
      alt="Bold"
      className="w-7 h-7"
    />
  );
  const Paperclip = () => (
    <img
      src="data:image/svg+xml;base64,"
      alt="Bold"
      className="w-6 h-6"
    />
  );
  const ImageIcon = () => (
    <img
      src="data:image/svg+xml;base64,"
      alt="Bold"
      className="w-6 h-6"
    />
  );
  const LinkIcon = () => (
    <img
      src="data:image/svg+xml;base64,"
      alt="Bold"
      className="w-5 h-5"
    />
  );
  const Undos = () => (
    <img
      src="data:image/svg+xml;base64,"
      alt="Bold"
      className="w-5 h-5"
    />
  );
  const Redos = () => (
    <img
      src="data:image/svg+xml;base64,"
      alt="Bold"
      className="w-5 h-5"
    />
  );
  const TableIcon = () => (
    <img
      src="data:image/svg+xml;base64,"
      alt="Bold"
      className="w-6 h-6"
    />
  );
  const Smile = () => (
    <img
      src="data:image/svg+xml;base64,"
      alt="Bold"
      className="w-6 h-6"
    />
  );
  const Search = () => (
    <img
      src="data:image/svg+xml;base64,"
      alt="Bold"
      className="w-6 h-6"
    />
  );
  const ListIndentIncrease = () => (
    <img
      src="data:image/svg+xml;base64,"
      alt="Bold"
      className="w-5 h-5"
    />
  );
  const ListIndentDecrease = () => (
    <img
      src="data:image/svg+xml;base64,"
      alt="Bold"
      className="w-5 h-5"
    />
  );
  const X = () => (
    <img
      src="data:image/svg+xml;base64,"
      alt="Bold"
      className="w-5 h-5"
    />
  );

  const Tagging = () => {
    return (
      <img className="w-[1.3px]" src="data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iMSIgaGVpZ2h0PSIzMiIgdmlld0JveD0iMCAwIDEgMzIiIGZpbGw9Im5vbmUiIHhtbG5zPSJodHRwOi8vd3d3LnczLm9yZy8yMDAwL3N2ZyI+CjxyZWN0IHdpZHRoPSIxIiBoZWlnaHQ9IjMyIiBmaWxsPSIjRTZFNkU2Ii8+Cjwvc3ZnPgo=" />
    )
  }

  const addTable = () => {
    editor.chain().focus().insertTable({ rows: 3, cols: 3, withHeaderRow: true }).run();
  };

  const handleSearch = () => {
    const query = window.prompt("Cari teks:");
    if (!query) return;
    const html = editor.getHTML();
    const regex = new RegExp(`(${query})`, "gi");
    const highlighted = html.replace(regex, `<mark style="background-color: yellow;">$1</mark>`);
    editor.commands.setContent(highlighted, false);
  };

  const addLink = () => {
    if (!editor) return;

    const url = window.prompt("Masukkan URL:");
    if (!url) return;

    const displayText = url.replace(/^https?:\/\//, "");
    const hasSelection = editor.state.selection.from !== editor.state.selection.to;

    if (!hasSelection) {
      editor
        .chain()
        .focus()
        .insertContent(`<a href="${url}" target="_blank">${displayText}</a>`)
        .run();
    } else {
      editor.chain().focus().setLink({ href: url }).run();
    }
  };


  const addEmoji = (emojiData) => {
    editor.chain().focus().insertContent(emojiData.emoji).run();
    setShowEmoji(false);
  };

  const activeClass = (condition) => (condition ? "active" : "");


  return (
    <div className="toolbar">
      <select
        onChange={(e) => editor.chain().focus().setFontFamily(e.target.value).run()}
        className="toolbar-select"
      >
        <option value="Arial">Arial</option>
        <option value="'Courier New'">Courier New</option>
        <option value="'Times New Roman'">Times New Roman</option>
        <option value="'Open Sans'">Open Sans</option>
        <option value="'Roboto'">Roboto</option>
      </select>
      <Tagging />
      <select
        onChange={(e) => editor.chain().focus().setFontSize(e.target.value).run()}
        className="toolbar-select"
      >
        {[12, 14, 16, 18, 20, 24, 28, 32].map((size) => (
          <option key={size} value={size}>{size}</option>
        ))}
      </select>
      <Tagging />
      <button className={activeClass(editor.isActive("bold"))} onClick={() => editor.chain().focus().toggleBold().run()} title="Bold"><Bold /></button>
      <button className={activeClass(editor.isActive("underline"))} onClick={() => editor.chain().focus().toggleUnderline().run()} title="Underline"><Underline /></button>
      <button className={activeClass(editor.isActive("italic"))} onClick={() => editor.chain().focus().toggleItalic().run()} title="Italic"><Italic /></button>
      <button className={activeClass(editor.isActive("orderedList"))} onClick={() => editor.chain().focus().toggleOrderedList().run()} title="Numbered list"><ListOrdered /></button>
      <button className={activeClass(editor.isActive("bulletList"))} onClick={() => editor.chain().focus().toggleBulletList().run()} title="Bulleted list"><List /></button>
      <Tagging />
      <button onClick={() => editor.chain().focus().setTextAlign("left").run()} title="Align left"><AlignLeft /></button>
      <button onClick={() => editor.chain().focus().setTextAlign("center").run()} title="Align center"><AlignCenter /></button>
      <button onClick={() => editor.chain().focus().setTextAlign("right").run()} title="Align right"><AlignRight /></button>
      <button onClick={() => editor.chain().focus().setTextAlign("justify").run()} title="Justify"><AlignJustify /></button>
      <Tagging />
      <button onClick={addAttachment} title="Attach File">
        <Paperclip />
      </button>
      <input
        type="file"
        ref={attachmentInputRef}
        onChange={handleAttachmentChange}
        style={{ display: "none" }}
      />
      <button onClick={addImage} title="Insert image"><ImageIcon /></button>
      <input type="file" accept="image/*" ref={fileInputRef} onChange={handleFileChange} style={{ display: "none" }} />
      <button onClick={addLink} title="Insert link"><LinkIcon /></button>
      <Tagging />
      <button onClick={() => editor.chain().focus().undo().run()} title="Undo"><Undos /></button>
      <button onClick={() => editor.chain().focus().redo().run()} title="Redo"><Redos /></button>
      <Tagging />
      <button className={activeClass(editor.isActive("highlight"))} onClick={() => editor.chain().focus().toggleHighlight().run()} title="Highlight"><Highlighter /></button>
      <button onClick={addTable} title="Insert table"><TableIcon /></button>
      <button className={activeClass(editor.isActive("strike"))} onClick={() => editor.chain().focus().toggleStrike().run()} title="Strikethrough"><Strikethrough /></button>
      <Tagging />
      <button onClick={() => setShowEmoji(!showEmoji)} title="Insert emoji"><Smile /></button>
      {showEmoji && (
        <div className="emoji-popup">
          <EmojiPicker onEmojiClick={addEmoji} />
        </div>
      )}
      <button onClick={() => editor.chain().focus().sinkListItem('listItem').run()} title="Increase indent"><ListIndentIncrease /></button>
      <button onClick={() => editor.chain().focus().liftListItem('listItem').run()} title="Decrease indent"><ListIndentDecrease /></button>
      <Tagging />
      <button onClick={handleSearch} title="Search"><Search /></button>
      <button
        className={activeClass(editor.isActive('subscript'))}
        onClick={() => editor.chain().focus().toggleSubscript().run()}
        title="Subscript"
      >
        <X />
      </button>
    </div>
  );
}
